import React, { useState } from 'react';

let intervalID;
let index = 0;

const App = ({ items }) => {
  const [delay, setDelay] = useState(1000);
  const [_, setCurrentIndex] = useState(0);
  const currentImage = items[index].imageUrl;

  const handleChange = ({ target: { value } }) => {
    setDelay(value * 1000);
  };

  const handleStart = () => {
    intervalID = setInterval(() => {
      if (index < items.length - 1) {
        index++;
        setCurrentIndex(index);
      } else {
        index = 0;
        setCurrentIndex(index);
      }
    }, delay);
  };

  const handleStop = (intervalID) => {
    clearInterval(intervalID);
  };

  return (
    <div className="w-96">
      <img className="object-contain h-96 w-96 mb-8" src={currentImage} />
      Slideshow delay (in seconds)
      <input
        onChange={handleChange}
        className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
        type="number"
      />
      <button
        className="bg-gray-400 hover:bg-gray-600 text-white text-xs font-semibold py-2 px-4 rounded mt-2"
        onClick={() => handleStart()}
      >
        Start
      </button>
      <button
        className="bg-gray-400 hover:bg-gray-600 text-white text-xs font-semibold py-2 px-4 rounded mt-2"
        onClick={() => handleStop(intervalID)}
      >
        Stop
      </button>
    </div>
  );
};

export default App;
